(function ($) {
	$.fn.formbuilder = function (options) {
		// Extend the configuration options with user-provided
		var defaults = {
			save_url: false,
			load_url: false,
			load_string: false,
			control_box_target: false,
			useJson: true, // XML as fallback
			serialize_prefix: 'frmb',
			messages: {
				save				: "Save",
				add_new_field		: "Add New Field...",
				text				: "Text Field",
				title				: "Title",
				paragraph			: "Paragraph",
				checkboxes			: "Checkboxes",
				radio				: "Radio",
				select				: "Select List",
				text_field			: "Text Field",
				label				: "Label",
				paragraph_field		: "Paragraph Field",
				select_options		: "Select Options",
				add					: '<img src="/auto/wp-content/plugins/better-user-management/images/add.png" width="24" height="24" />',
				checkbox_group		: "Checkbox Group",
				remove_message		: "Are you sure you want to remove this element?",
				remove				: '<img src="/auto/wp-content/plugins/better-user-management/images/delete.png" width="24" height="24" />',
				radio_group			: "Radio Group",
				selections_message	: "Allow Multiple Selections",
				hide				: '<img src="/auto/wp-content/plugins/better-user-management/images/minimize.png" width="24" height="24" />',
				required			: "Required",
				show				: '<img src="/auto/wp-content/plugins/better-user-management/images/maximize.png" width="24" height="24" />',
				tip					: 'Tip',
				registration_page   : 'Show on registration?',
				id                  : 'ID ( uses title if left empty )'
			}
		};
		var opts = $.extend(defaults, options);
		var frmb_id = 'frmb-' + $('ul[id^=frmb-]').length++;
		return this.each(function () {
			var ul_obj = $(this); //.append('<ul id="' + frmb_id + '" class="frmb"></ul>').find('ul');
			var field = '';
			var field_type = '';
			var last_id = 1;
			var help;
			// Add a unique class to the current element
			$(ul_obj).addClass(frmb_id);
			// load existing form data
			if (opts.load_url) {
				$.ajax({
					type: "GET",
					url: opts.load_url,
					success: function (data) {
						if (opts.useJson) {
							fromJson(data);
						} else {
							fromXml(data);
						}
					}
				});
			}
			
			// Create form control select box and add into the editor
			var controlBox = function (target) {
					var select = '';
					var box_content = '';
					var save_button = '';
					var box_id = frmb_id + '-control-box';
					var save_id = frmb_id + '-save-button';
					// Add the available options
					/*select += '<div id="fbVal-0" class="widget"><div class="widget-top"><div class="widget-title"><h4>' + opts.messages.add_new_field + '</h4></div></div></div>';*/
					select += '<div id="fbVal-input_text" class="widget"><div class="widget-top"><div class="widget-title"><h4>' + opts.messages.text + '</h4></div></div></div>';
					select += '<div id="fbVal-textarea" class="widget"><div class="widget-top"><div class="widget-title"><h4>' + opts.messages.paragraph + '</h4></div></div></div>';
					select += '<div id="fbVal-checkbox" class="widget"><div class="widget-top"><div class="widget-title"><h4>' + opts.messages.checkboxes + '</h4></div></div></div>';
					select += '<div id="fbVal-radio" class="widget"><div class="widget-top"><div class="widget-title"><h4>' + opts.messages.radio + '</h4></div></div></div>';
					select += '<div id="fbVal-select" class="widget"><div class="widget-top"><div class="widget-title"><h4>' + opts.messages.select + '</h4></div></div></div>';
					// Build the control box and search button content
					box_content = '<div id="fbMainDiv">' + select + '</div>';
					save_button = '<input type="submit" id="' + save_id + '" class="frmb-submit" value="' + opts.messages.save + '"/>';
					// Insert the control box into page
					if (!target) {
						$(ul_obj).before(box_content);
					} else {
						$(target).append(box_content);
					}
					// Insert the search button
					//$(ul_obj).after(save_button);
					// Set the form save action
					$('#fbSaveButton').click(function () { //$('#' + save_id).click(function () {
						save();
						return false;
					});
					
					// Add a callback to the select element
					$('#fbMainDiv > div').click(function () {
						appendNewField( $(this).attr('id').replace( 'fbVal-', '' ) );
						//$(this).val(0).blur();
						// This solves the scrollTo dependency
						$('html, body').animate({
							scrollTop: $('#frm-' + (last_id - 1) + '-item').offset().top
						}, 500);
						return false;
					});
				}(opts.control_box_target);
			// XML parser to build the form builder
			var fromXml = function (xml) {
					var values = '';
					var options = false;
					var required = false;
					$(xml).find('field').each(function () {
						// checkbox type
						if ($(this).attr('type') === 'checkbox') {
							options = [$(this).attr('title')];
							values = [];
							$(this).find('checkbox').each(function () {
								values.push([$(this).text(), $(this).attr('checked')]);
							});
						}
						// radio type
						else if ($(this).attr('type') === 'radio') {
							options = [$(this).attr('title')];
							values = [];
							$(this).find('radio').each(function () {
								values.push([$(this).text(), $(this).attr('checked')]);
							});
						}
						// select type
						else if ($(this).attr('type') === 'select') {
							options = [$(this).attr('title'), $(this).attr('multiple')];
							values = [];
							$(this).find('option').each(function () {
								values.push([$(this).text(), $(this).attr('checked')]);
							});
						}
						else {
							values = $(this).text();
						}
						appendNewField($(this).attr('type'), values, options, $(this).attr('required'));
					});
				};
			// Json parser to build the form builder
			var fromJson = function (json) {
					var values = '';
					var options = false;
					var required = false;
					// Parse json
					$(json).each(function () {
						// checkbox type
						if (this.cssClass === 'checkbox') {
							options = [this.title];
							values = [];
							$.each(this.values, function () {
								values.push([this.value, this.baseline]);
							});
						}
						// radio type
						else if (this.cssClass === 'radio') {
							options = [this.title];
							values = [];
							$.each(this.values, function () {
								values.push([this.value, this.baseline]);
							});
						}
						// select type
						else if (this.cssClass === 'select') {
							options = [this.title, this.multiple];
							values = [];
							$.each(this.values, function () {
								values.push([this.value, this.baseline]);
							});
						}
						else {
							values = [this.values];
						}
						extra = { 'tip':this.tip, 'register':this.register, 'id':this.id };
						appendNewField(this.cssClass, values, options, this.required, extra);
					});
				};
			// Wrapper for adding a new field
			var appendNewField = function (type, values, options, required, extra) {
					field = '';
					field_type = type;
					if (typeof (values) === 'undefined') {
						values = '';
					}
					switch (type) {
					case 'input_text':
						appendTextInput(values, required, extra);
						break;
					case 'textarea':
						appendTextarea(values, required, extra);
						break;
					case 'checkbox':
						appendCheckboxGroup(values, options, required, extra);
						break;
					case 'radio':
						appendRadioGroup(values, options, required, extra);
						break;
					case 'select':
						appendSelectList(values, options, required, extra);
						break;
					}
				};
			// single line input type="text"
			var appendTextInput = function (values, required, extra) {
					field += '<label>' + opts.messages.label + '</label>';
					field += '<input class="fld-title" id="title-' + last_id + '" type="text" value="' + values + '" />';
					appendFieldLi(opts.messages.text, field, required, extra);
				};
			// multi-line textarea
			var appendTextarea = function (values, required, extra) {
					field += '<label>' + opts.messages.label + '</label>';
					field += '<input type="text" name="title" value="' + values + '" />';
					appendFieldLi(opts.messages.paragraph_field, field, required, extra);
				};
			// adds a checkbox element
			var appendCheckboxGroup = function (values, options, required, extra) {
					var title = '';
					if (typeof (options) === 'object') {
						title = options[0];
					}
					field += '<div class="chk_group">';
					field += '<div class="frm-fld"><label>' + opts.messages.title + '</label>';
					field += '<input type="text" name="title" value="' + title + '" /></div>';
					field += '<div class="false-label">' + opts.messages.select_options + '</div>';
					field += '<div class="fields">';
					if (typeof (values) === 'object') {
						for (i = 0; i < values.length; i++) {
							field += checkboxFieldHtml(values[i]);
						}
					}
					else {
						field += checkboxFieldHtml('');
					}
					field += '<div class="add-area"><a href="#" class="add add_ck">' + opts.messages.add + '</a></div>';
					field += '</div>';
					field += '</div>';
					appendFieldLi(opts.messages.checkbox_group, field, required, extra);
				};
			// Checkbox field html, since there may be multiple
			var checkboxFieldHtml = function (values) {
					var checked = false;
					var value = '';
					if (typeof (values) === 'object') {
						value = values[0];
						checked = ( values[1] === 'false' || values[1] === 'undefined' ) ? false : true;
					}
					field = '';
					field += '<div>';
					field += '<input type="checkbox"' + (checked ? ' checked="checked"' : '') + ' />';
					field += '<input type="text" value="' + value + '" />';
					field += '<a href="#" class="remove" title="' + opts.messages.remove_message + '">' + opts.messages.remove + '</a>';
					field += '</div>';
					return field;
				};
			// adds a radio element
			var appendRadioGroup = function (values, options, required, extra) {
					var title = '';
					if (typeof (options) === 'object') {
						title = options[0];
					}
					field += '<div class="rd_group">';
					field += '<div class="frm-fld"><label>' + opts.messages.title + '</label>';
					field += '<input type="text" name="title" value="' + title + '" /></div>';
					field += '<div class="false-label">' + opts.messages.select_options + '</div>';
					field += '<div class="fields">';
					if (typeof (values) === 'object') {
						for (i = 0; i < values.length; i++) {
							field += radioFieldHtml(values[i], 'frm-' + last_id + '-fld');
						}
					}
					else {
						field += radioFieldHtml('', 'frm-' + last_id + '-fld');
					}
					field += '<div class="add-area"><a href="#" class="add add_rd">' + opts.messages.add + '</a></div>';
					field += '</div>';
					field += '</div>';
					appendFieldLi(opts.messages.radio_group, field, required, extra);
				};
			// Radio field html, since there may be multiple
			var radioFieldHtml = function (values, name) {
					var checked = false;
					var value = '';
					if (typeof (values) === 'object') {
						value = values[0];
						checked = ( values[1] === 'false' || values[1] === 'undefined' ) ? false : true;
					}
					field = '';
					field += '<div>';
					field += '<input type="radio"' + (checked ? ' checked="checked"' : '') + ' name="radio_' + name + '" />';
					field += '<input type="text" value="' + value + '" />';
					field += '<a href="#" class="remove" title="' + opts.messages.remove_message + '">' + opts.messages.remove + '</a>';
					field += '</div>';
					return field;
				};
			// adds a select/option element
			var appendSelectList = function (values, options, required, extra) {
					var multiple = false;
					var title = '';
					if (typeof (options) === 'object') {
						title = options[0];
						multiple = options[1] === 'true' ? true : false;
					}
					field += '<div class="opt_group">';
					field += '<div class="frm-fld"><label>' + opts.messages.title + '</label>';
					field += '<input type="text" name="title" value="' + title + '" /></div>';
					field += '';
					field += '<div class="false-label">' + opts.messages.select_options + '</div>';
					field += '<div class="fields">';
					field += '<input type="checkbox" name="multiple"' + (multiple ? 'checked="checked"' : '') + '>';
					field += '<label class="auto">' + opts.messages.selections_message + '</label>';
					if (typeof (values) === 'object') {
						for (i = 0; i < values.length; i++) {
							field += selectFieldHtml(values[i], multiple);
						}
					}
					else {
						field += selectFieldHtml('', multiple);
					}
					field += '<div class="add-area"><a href="#" class="add add_opt">' + opts.messages.add + '</a></div>';
					field += '</div>';
					field += '</div>';
					appendFieldLi(opts.messages.select, field, required, extra);
				};
			// Select field html, since there may be multiple
			var selectFieldHtml = function (values, multiple) {
					if (multiple) {
						return checkboxFieldHtml(values);
					}
					else {
						return radioFieldHtml(values);
					}
				};
			// Appends the new field markup to the editor
			var appendFieldLi = function (title, field_html, required, help) {
					if (required) {
						required = required === 'true' ? true : false;
					}
					var li = '';
					li += '<li id="frm-' + last_id + '-item" class="' + field_type + '">';
					li += '<div class="legend">';
					li += '<a id="frm-' + last_id + '" class="toggle-form" href="#">' + opts.messages.hide + '</a> ';
					li += '<a id="del_' + last_id + '" class="del-button delete-confirm" href="#" title="' + opts.messages.remove_message + '"><span>' + opts.messages.remove + '</span></a>';
					li += '<strong id="txt-title-' + last_id + '">' + title + '</strong></div>';
					li += '<div id="frm-' + last_id + '-fld" class="frm-holder">';
					li += '<div class="frm-elements">';
					
					//required checkbox
					li += '<div class="frm-fld"><label for="required-' + last_id + '">' + opts.messages.required + '</label>';
					li += '<input class="required" type="checkbox" value="1" name="required-' + last_id + '" id="required-' + last_id + '"' + (required ? ' checked="checked"' : '') + ' /></div>';
					
					//show on registration checkbox
					li += '<div class="frm-fld"><label for="register-' + last_id + '">' + opts.messages.registration_page + '</label>';
					li += '<input class="fld-register" type="checkbox" value="1" name="register-' + last_id + '" id="register-' + last_id + '"' + (help && help.register == 'true' ? ' checked="checked"' : '') + ' /></div>';
					
					//tip box
					li += '<div class="frm-fld"><label for="tip-' + last_id + '">' + opts.messages.tip + '</label>';
					li += '<input class="fld-tip" type="text" value="' + ( help ? help.tip : '' ) + '" name="tip-' + last_id + '" id="tip-' + last_id + '" /></div>';
					
					//id box
					li += '<div class="frm-fld"><label for="id-' + last_id + '">' + opts.messages.id + '</label>';
					li += '<input class="fld-id" type="text" value="' + ( help ? help.id : '' ) + '" name="id-' + last_id + '" id="id-' + last_id + '" /></div>';
					
					li += field;
					li += '</div>';
					li += '</div>';
					li += '</li>';
					$('ul.form-builder-container').append(li); //$(ul_obj).append(li);
					$('#frm-' + last_id + '-item').hide();
					$('#frm-' + last_id + '-item').animate({
						opacity: 'show',
						height: 'show'
					}, 'slow');
					last_id++;
				};
			// handle field delete links
			$('.remove').live('click', function () {
				$(this).parent('div').animate({
					opacity: 'hide',
					height: 'hide',
					marginBottom: '0px'
				}, 'fast', function () {
					$(this).remove();
				});
				return false;
			});
			// handle field display/hide
			$('.toggle-form').live('click', function () {
				var target = $(this).attr("id");
				if (!$(this).hasClass('closed')) {
					$(this).removeClass('open').addClass('closed').html(opts.messages.show);
					$('#' + target + '-fld').animate({
						opacity: 'hide',
						height: 'hide'
					}, 'slow');
					return false;
				}
				if ($(this).hasClass('closed')) {
					$(this).removeClass('closed').addClass('open').html(opts.messages.hide);
					$('#' + target + '-fld').animate({
						opacity: 'show',
						height: 'show'
					}, 'slow');
					return false;
				}
				return false;
			});
			// handle delete confirmation
			$('.delete-confirm').live('click', function () {
				var delete_id = $(this).attr("id").replace(/del_/, '');
				if (confirm($(this).attr('title'))) {
					$('#frm-' + delete_id + '-item').animate({
						opacity: 'hide',
						height: 'hide',
						marginBottom: '0px'
					}, 'slow', function () {
						$(this).remove();
					});
				}
				return false;
			});
			// Attach a callback to add new checkboxes
			$('.add_ck').live('click', function () {
				$(this).parent().before(checkboxFieldHtml());
				return false;
			});
			// Attach a callback to add new options
			$('.add_opt').live('click', function () {
				$(this).parent().before(selectFieldHtml('', false));
				return false;
			});
			// Attach a callback to add new radio fields
			$('.add_rd').live('click', function () {
				$(this).parent().before(radioFieldHtml(false, $(this).parents('.frm-holder').attr('id')));
				return false;
			});
			// saves the serialized data to the server 
			var save = function () {
					if (opts.save_url) {
						$.ajax({
							type: "POST",
							url: opts.save_url,
							data: $(ul_obj).serializeFormList({
								prepend: opts.serialize_prefix
							}),
							success: function (xml) {}
						});
					} else if( $('#fbJsonField').length ) {
						$('#fbJsonField').val( $('ul.form-builder-container').serializeFormList({ prepend: opts.serialize_prefix }) ).parent().submit();
					}
				};
				
			if (opts.load_string) {
				if (opts.useJson) {
					fromJson( $.parseJSON( opts.load_string ) );
				} else {
					fromXml(opts.load_string);
				}
			}
			
			$('ul.form-builder-container').sortable();
			//$('ul.form-builder-container').disableSelection();
		});
	};
})(jQuery);
/**
 * jQuery Form Builder List Serialization Plugin
 * Copyright (c) 2009 Mike Botsko, Botsko.net LLC (http://www.botsko.net)
 * Originally designed for AspenMSM, a CMS product from Trellis Development
 * Licensed under the MIT (http://www.opensource.org/licenses/mit-license.php)
 * Copyright notice and license must remain intact for legal use
 * Modified from the serialize list plugin
 * http://www.botsko.net/blog/2009/01/jquery_serialize_list_plugin/
 */
(function ($) {
	$.fn.serializeFormList = function (options) {
		// Extend the configuration options with user-provided
		var defaults = {
			prepend: 'ul',
			is_child: false,
			attributes: ['class']
		};
		var opts = $.extend(defaults, options);
		if (!opts.is_child) {
			opts.prepend = '&' + opts.prepend;
		}
		var serialStr = '';
		// Begin the core plugin
		this.each(function () {
			var ul_obj = this;
			var li_count = 0;
			var c = 1;
			$(this).children().each(function () {
				for (att = 0; att < opts.attributes.length; att++) {
					var key = (opts.attributes[att] === 'class' ? 'cssClass' : opts.attributes[att]);
					serialStr += opts.prepend + '[' + li_count + '][' + key + ']=' + encodeURIComponent($(this).attr(opts.attributes[att]));
					// append the form field values
					if (opts.attributes[att] === 'class') {
						//required
						serialStr += opts.prepend + '[' + li_count + '][required]=' + encodeURIComponent($('#' + $(this).attr('id') + ' input.required').attr('checked'));
						
						//registration
						serialStr += opts.prepend + '[' + li_count + '][register]=' + encodeURIComponent($('#' + $(this).attr('id') + ' input.fld-register').attr('checked'));
						
						//tip
						serialStr += opts.prepend + '[' + li_count + '][tip]=' + encodeURIComponent($('#' + $(this).attr('id') + ' input.fld-tip').val());
						
						//get id
						var the_id = $('#' + $(this).attr('id') + ' input.fld-id').val();
						
						//field specific info
						var the_title = '';
						switch ($(this).attr(opts.attributes[att])) {
						case 'input_text':
							the_title = $('#' + $(this).attr('id') + ' input.fld-title').val();
							serialStr += opts.prepend + '[' + li_count + '][values]=' + encodeURIComponent($('#' + $(this).attr('id') + ' input.fld-title').val());
							break;
						case 'textarea':
							the_title = $('#' + $(this).attr('id') + ' input[type=text]').val();
							serialStr += opts.prepend + '[' + li_count + '][values]=' + encodeURIComponent($('#' + $(this).attr('id') + ' input[name="title"]').val());
							break;
						case 'checkbox':
							the_title = $('#' + $(this).attr('id') + ' input[name="title"]').val();
							c = 1;
							serialStr += opts.prepend + '[' + li_count + '][title]=' + encodeURIComponent($('#' + $(this).attr('id') + ' input[name="title"]').val());
							$('#' + $(this).attr('id') + ' div.fields input[type=text]').each(function () {
								serialStr += opts.prepend + '[' + li_count + '][values][' + c + '][value]=' + encodeURIComponent($(this).val());
								serialStr += opts.prepend + '[' + li_count + '][values][' + c + '][baseline]=' + $(this).prev().attr('checked');
								c++;
							});
							break;
						case 'radio':
							the_title = $('#' + $(this).attr('id') + ' input[name="title"]').val();
							c = 1;
							serialStr += opts.prepend + '[' + li_count + '][title]=' + encodeURIComponent($('#' + $(this).attr('id') + ' input[name="title"]').val());
							$('#' + $(this).attr('id') + ' div.fields input[type=text]').each(function () {
								serialStr += opts.prepend + '[' + li_count + '][values][' + c + '][value]=' + encodeURIComponent($(this).val());
								serialStr += opts.prepend + '[' + li_count + '][values][' + c + '][baseline]=' + $(this).prev().attr('checked');
								c++;
							});
							break;
						case 'select':
							the_title = $('#' + $(this).attr('id') + ' input[name="title"]').val();
							c = 1;
							serialStr += opts.prepend + '[' + li_count + '][multiple]=' + $('#' + $(this).attr('id') + ' input[name=multiple]').attr('checked');
							serialStr += opts.prepend + '[' + li_count + '][title]=' + encodeURIComponent($('#' + $(this).attr('id') + ' input[name="title"]').val());
							$('#' + $(this).attr('id') + ' div.fields input[type=text]').each(function () {
								serialStr += opts.prepend + '[' + li_count + '][values][' + c + '][value]=' + encodeURIComponent($(this).val());
								serialStr += opts.prepend + '[' + li_count + '][values][' + c + '][baseline]=' + $(this).prev().attr('checked');
								c++;
							});
							break;
						}
						
						//id
						if( ( the_id == 'undefined' || the_id == '' ) && the_title != '' )
							the_id = the_title.replace( /[\s]/g, '-' ).replace(/[^\w-_]|_/g, "").toLowerCase();
						serialStr += opts.prepend + '[' + li_count + '][id]=' + encodeURIComponent(the_id);
					}
				}
				li_count++;
			});
		});
		return (serialStr);
	};
})(jQuery);